-- Created by TSQL.APP Agent (https://www.ux1.nl)
-- 1. Pre-declare all sp parameters
DECLARE @text NVARCHAR(MAX);
DECLARE @class NVARCHAR(MAX);
DECLARE @title NVARCHAR(MAX);
DECLARE @name NVARCHAR(MAX);
DECLARE @value NVARCHAR(MAX);
DECLARE @valueout NVARCHAR(MAX);
DECLARE @placeholder NVARCHAR(MAX);
DECLARE @inline BIT;
DECLARE @html NVARCHAR(MAX);
DECLARE @type NVARCHAR(128);
DECLARE @number_min INT;
DECLARE @number_max INT;
DECLARE @direct_post BIT;
DECLARE @label NVARCHAR(MAX);

-- 2. Business variables
DECLARE @RedValue INT;
DECLARE @GreenValue INT;
DECLARE @BlueValue INT;
DECLARE @ColorName NVARCHAR(100);
DECLARE @SaveButton NVARCHAR(MAX);
DECLARE @DebugMode BIT = 0;
DECLARE @SavedColors NVARCHAR(MAX);
DECLARE @ColorHex NVARCHAR(7);
DECLARE @Message NVARCHAR(MAX);
DECLARE @JsonState NVARCHAR(MAX);
DECLARE @CurrentColor NVARCHAR(100);

-- 3. State synchronization
DECLARE @RedValueFromModal NVARCHAR(MAX);
DECLARE @GreenValueFromModal NVARCHAR(MAX);
DECLARE @BlueValueFromModal NVARCHAR(MAX);

EXEC sp_api_modal_get_value @name=N'@RedValue', @value=@RedValueFromModal OUT;
IF @RedValueFromModal IS NOT NULL SET @RedValue = TRY_CAST(@RedValueFromModal AS INT);

EXEC sp_api_modal_get_value @name=N'@GreenValue', @value=@GreenValueFromModal OUT;
IF @GreenValueFromModal IS NOT NULL SET @GreenValue = TRY_CAST(@GreenValueFromModal AS INT);

EXEC sp_api_modal_get_value @name=N'@BlueValue', @value=@BlueValueFromModal OUT;
IF @BlueValueFromModal IS NOT NULL SET @BlueValue = TRY_CAST(@BlueValueFromModal AS INT);

EXEC sp_api_modal_get_value @name=N'@ColorName', @value=@ColorName OUT;
EXEC sp_api_modal_get_value @name=N'@SaveButton', @value=@SaveButton OUT;
EXEC sp_api_modal_get_value @name=N'@SavedColors', @value=@SavedColors OUT;

--if @SavedColors IS NULL SET @SavedColors = N'[{"ColorName":"TSQL Pink","RedValue":231,"GreenValue":200,"BlueValue":251,"HexValue":"#E7C8FB"}]'
	

-- 4. Initialize values if needed
IF @RedValue IS NULL SET @RedValue = 231;
IF @GreenValue IS NULL SET @GreenValue = 200;
IF @BlueValue IS NULL SET @BlueValue = 251;
IF @SavedColors IS NULL SET @SavedColors = N'[{"ColorName":"TSQL Pink","RedValue":231,"GreenValue":200,"BlueValue":251,"HexValue":"#E7C8FB"}]';

-- Calculate color hex value
SET @ColorHex = N'#' + 
	RIGHT(N'0' + CONVERT(NVARCHAR(2), CONVERT(VARBINARY(1), @RedValue), 2), 2) +
	RIGHT(N'0' + CONVERT(NVARCHAR(2), CONVERT(VARBINARY(1), @GreenValue), 2), 2) +
	RIGHT(N'0' + CONVERT(NVARCHAR(2), CONVERT(VARBINARY(1), @BlueValue), 2), 2);

-- Create a temporary table for saved colors
IF OBJECT_ID('tempdb..#SavedColors') IS NOT NULL DROP TABLE #SavedColors;
CREATE TABLE #SavedColors (
	ColorName NVARCHAR(100),
	RedValue INT,
	GreenValue INT,
	BlueValue INT,
	HexValue NVARCHAR(7)
);

-- Populate from JSON if exists
IF @SavedColors != N'[]'
BEGIN
	DECLARE @JsonImportSql NVARCHAR(MAX);
	
	SET @JsonImportSql = N'INSERT INTO #SavedColors
	SELECT 
		c.ColorName,
		c.RedValue,
		c.GreenValue,
		c.BlueValue,
		c.HexValue
	FROM OPENJSON(''' + @SavedColors + ''')
	WITH (
		ColorName NVARCHAR(100) ''$.ColorName'',
		RedValue INT ''$.RedValue'',
		GreenValue INT ''$.GreenValue'',
		BlueValue INT ''$.BlueValue'',
		HexValue NVARCHAR(7) ''$.HexValue''
	) c';
	
	EXEC sp_api_exec @sql = @JsonImportSql;
END

-- Display interface
SET @title = N'Color Picker Demo';
EXEC sp_api_modal_text @text = @title, @class = N'h3';
EXEC sp_api_modal_text @text = N'Create and save your favorite colors', @class = N'text-muted mb-3';

-- Color preview
DECLARE @ColorPreviewHtml NVARCHAR(MAX);
SET @ColorPreviewHtml = CONCAT(N'<div style="width:100%;height:100px;background-color:', @ColorHex, N';border-radius:8px;margin-bottom:15px;"></div>');
EXEC sp_api_modal_html @name = N'@ColorPreview', @html = @ColorPreviewHtml;

-- RGB sliders
DECLARE @RedLabel NVARCHAR(MAX);
DECLARE @GreenLabel NVARCHAR(MAX);
DECLARE @BlueLabel NVARCHAR(MAX);
DECLARE @RedValueText NVARCHAR(3);
DECLARE @GreenValueText NVARCHAR(3);
DECLARE @BlueValueText NVARCHAR(3);

-- Prepare text values first (following mandated practices)
SET @RedValueText = CAST(@RedValue AS NVARCHAR(3));
SET @GreenValueText = CAST(@GreenValue AS NVARCHAR(3));
SET @BlueValueText = CAST(@BlueValue AS NVARCHAR(3));

SET @RedLabel = CONCAT(N'Red: ', @RedValueText);
SET @GreenLabel = CONCAT(N'Green: ', @GreenValueText);
SET @BlueLabel = CONCAT(N'Blue: ', @BlueValueText);

-- Now pass the prepared values to the procedures
EXEC sp_api_modal_text @text = @RedLabel, @class = N'mb-1', @inline = 1;
EXEC sp_api_modal_input @name = N'@RedValue', @value = @RedValue, @type = N'range', @number_min = 0, @number_max = 255;

EXEC sp_api_modal_text @text = @GreenLabel, @class = N'mb-1', @inline = 1;
EXEC sp_api_modal_input @name = N'@GreenValue', @value = @GreenValue, @type = N'range', @number_min = 0, @number_max = 255;

EXEC sp_api_modal_text @text = @BlueLabel, @class = N'mb-1', @inline = 1;
EXEC sp_api_modal_input @name = N'@BlueValue', @value = @BlueValue, @type = N'range', @number_min = 0, @number_max = 255;

-- Debug mode toggle
EXEC sp_api_modal_switch 
	@name = N'@DebugMode', 
	@value = @DebugMode OUT, 
	@label = N'Show Snapshot Code', 
	@inline = 1,
	@direct_post = 1;

-- Display debug information if enabled
IF @DebugMode = 1
BEGIN
	EXEC sp_api_modal_text @text = N'Snapshot Code (Copy & Paste to Create a New Action):', @class = N'mt-3 mb-2 font-weight-bold';
	EXEC sp_api_modal_show_json_values
END

-- Saved colors table if any exist
IF EXISTS (SELECT 1 FROM #SavedColors)
BEGIN
	EXEC sp_api_modal_text @text = N'Your Saved Colors:', @class = N'mt-4 mb-2 font-weight-bold';
	EXEC sp_api_modal_table @name = N'@ColorTable', @tmptable = N'#SavedColors';
END

-- Save color form
EXEC sp_api_modal_text @text = N'Save Current Color:', @class = N'mt-4 mb-2 font-weight-bold';
EXEC sp_api_modal_input @name = N'@ColorName', @value = @ColorName OUT, @placeholder = N'Enter a name for this color';
EXEC sp_api_modal_button @name = N'@SaveButton', @value = N'Save Color', @valueout = @SaveButton OUT, @class = N'btn-success mt-2';

-- Handle save action
IF @SaveButton IS NOT NULL
BEGIN
	-- Validate input
	IF LEN(TRIM(ISNULL(@ColorName, N''))) < 2
	BEGIN
		SET @Message = N'Please enter a valid color name (at least 2 characters)';
		EXEC sp_api_toast @text = @Message, @class = N'btn-warning';
		RETURN;
	END
	
	-- Check if color name already exists
	IF EXISTS (SELECT 1 FROM #SavedColors WHERE ColorName = @ColorName)
	BEGIN
		SET @Message = N'A color with this name already exists';
		EXEC sp_api_toast @text = @Message, @class = N'btn-warning';
		RETURN;
	END
	
	-- Add new color to the table
	INSERT INTO #SavedColors (ColorName, RedValue, GreenValue, BlueValue, HexValue)
	VALUES (@ColorName, @RedValue, @GreenValue, @BlueValue, @ColorHex);
	
	-- Get updated JSON for state persistence
	SELECT @SavedColors = (SELECT * FROM #SavedColors FOR JSON PATH);
	
	-- Reset input field and update state
	SET @JsonState = (
		SELECT 
			[@RedValue]=@RedValue, 
			[@GreenValue]=@GreenValue,
			[@BlueValue]=@BlueValue,
			[@SavedColors]=@SavedColors,
			[@ColorName]=NULL
		FOR JSON PATH, WITHOUT_ARRAY_WRAPPER
	);
	
	-- Show success message and restart
	SET @Message = N'Color "' + @ColorName + N'" saved successfully!';
	EXEC sp_api_toast @text = @Message, @class = N'btn-success';
	EXEC sp_api_modal_restart @values = @JsonState;
	RETURN;
END

EXEC sp_api_modal_modal @class=N'w100', @printStyle=N'font-size:70%', @landscape=1
-- Eventueel als global action:  CTRL-ALT-E

-- SECTION 1: Variable declarations (following mandated practices)
DECLARE @text NVARCHAR(MAX);
DECLARE @class NVARCHAR(MAX);
DECLARE @html NVARCHAR(MAX);
DECLARE @ButtonValue NVARCHAR(MAX);
DECLARE @ButtonValueOut NVARCHAR(MAX);
DECLARE @name NVARCHAR(MAX);
DECLARE @description NVARCHAR(MAX);

-- Business variables
DECLARE @NodesJson NVARCHAR(MAX);
DECLARE @LinksJson NVARCHAR(MAX);
DECLARE @FinalJson NVARCHAR(MAX);
DECLARE @ErrorMsg NVARCHAR(MAX);
DECLARE @Message NVARCHAR(MAX);
DECLARE @Title NVARCHAR(MAX);
DECLARE @CurrentTable NVARCHAR(128);
DECLARE @CardTitle NVARCHAR(MAX);
DECLARE @TempSql NVARCHAR(MAX);
DECLARE @TablesList NVARCHAR(MAX);
DECLARE @RelationshipDescription NVARCHAR(MAX);

-- Synchronize with modal state
EXEC sp_api_modal_get_value @name=N'@ButtonValue', @value=@ButtonValue OUT;

    -- Get current card info
    SET @CurrentTable = ISNULL(@basetable, @tablename);
    IF @CurrentTable IS NULL SET @CurrentTable = @card_name;
    
    -- Set title
    SET @CardTitle = ISNULL(@card_name, 'Current Card');
    SET @Title = N'Database ERD for ' + @CardTitle;
    
    -- Create temporary tables for our node and link data
    IF OBJECT_ID('tempdb..#Nodes') IS NOT NULL DROP TABLE #Nodes;
    CREATE TABLE #Nodes (
        TableName NVARCHAR(128),
        TableColor NVARCHAR(50),
        Category NVARCHAR(50),
        ToolTip NVARCHAR(MAX)
    );
    
    IF OBJECT_ID('tempdb..#Links') IS NOT NULL DROP TABLE #Links;
    CREATE TABLE #Links (
        FromTable NVARCHAR(128),
        ToTable NVARCHAR(128)
    );
    
    -- Add the current table
    INSERT INTO #Nodes (TableName, TableColor, Category, ToolTip)
    VALUES (@CurrentTable, 'lightcoral', 'Current', 'Current Table: ' + @CurrentTable);
    
    -- Find parent tables (tables referenced by the current table)
    -- Create a temporary table to store foreign key relationships
    IF OBJECT_ID('tempdb..#ForeignKeyRels') IS NOT NULL DROP TABLE #ForeignKeyRels;
    CREATE TABLE #ForeignKeyRels (
        ParentTable NVARCHAR(128),
        ParentColumn NVARCHAR(128),
        ReferencedTable NVARCHAR(128),
        ReferencedColumn NVARCHAR(128)
    );
    
    -- Using sys_foreign_key_columns to find relationships
    SET @TempSql = N'
    INSERT INTO #ForeignKeyRels (ParentTable, ParentColumn, ReferencedTable, ReferencedColumn)
    SELECT 
        c.name AS ParentTable,
        cc.name AS ParentColumn,
        p.name AS ReferencedTable,
        pc.name AS ReferencedColumn
    FROM sys_foreign_key_columns fk
    JOIN sys_columns cc ON cc.object_id = fk.parent_object_id AND fk.parent_column_id = cc.column_id
    JOIN sys_columns pc ON pc.object_id = fk.referenced_object_id AND fk.referenced_column_id = pc.column_id
    JOIN sys_tables c ON c.object_id = fk.parent_object_id
    JOIN sys_tables p ON p.object_id = fk.referenced_object_id
    WHERE c.name = @CurrentTable OR p.name = @CurrentTable';
    
    EXEC sp_executesql @TempSql, N'@CurrentTable NVARCHAR(128)', @CurrentTable;
    
    -- Add parent tables (tables that the current table references)
    INSERT INTO #Nodes (TableName, TableColor, Category, ToolTip)
    SELECT DISTINCT 
        ReferencedTable,
        'lightblue',
        'Parent',
        'Parent Table: ' + ReferencedTable + ' (Referenced by column: ' + ParentColumn + ')'
    FROM #ForeignKeyRels
    WHERE ParentTable = @CurrentTable
    AND ReferencedTable <> @CurrentTable;
    
    -- Add links for parent relationships
    INSERT INTO #Links (FromTable, ToTable)
    SELECT DISTINCT 
        ParentTable,
        ReferencedTable
    FROM #ForeignKeyRels
    WHERE ParentTable = @CurrentTable
    AND ReferencedTable <> @CurrentTable;
    
    -- Find child tables (tables referencing the current table)
    -- Add child tables (tables that reference the current table)
    INSERT INTO #Nodes (TableName, TableColor, Category, ToolTip)
    SELECT DISTINCT 
        ParentTable,
        'lightgreen',
        'Child',
        'Child Table: ' + ParentTable + ' (References current table via column: ' + ParentColumn + ')'
    FROM #ForeignKeyRels
    WHERE ReferencedTable = @CurrentTable
    AND ParentTable <> @CurrentTable;
    
    -- Add links for child relationships
    INSERT INTO #Links (FromTable, ToTable)
    SELECT DISTINCT 
        ParentTable,
        ReferencedTable
    FROM #ForeignKeyRels
    WHERE ReferencedTable = @CurrentTable
    AND ParentTable <> @CurrentTable;
    
    -- Look for app-specific relationships in api_card_children
    IF OBJECT_ID('tempdb..#CardChildren') IS NOT NULL DROP TABLE #CardChildren;
    CREATE TABLE #CardChildren (
        ParentCard NVARCHAR(128),
        ChildCard NVARCHAR(128),
        RefColumn NVARCHAR(128)
    );
    
    -- Get relationships from api_card_children
    SET @TempSql = N'
    INSERT INTO #CardChildren (ParentCard, ChildCard, RefColumn)
    SELECT 
        p.name AS ParentCard,
        c.name AS ChildCard,
        acc.ref AS RefColumn
    FROM api_card_children acc
    JOIN api_card p ON acc.parent = p.id
    JOIN api_card c ON acc.child = c.id
    WHERE p.name = @CardTitle OR c.name = @CardTitle';
    
    EXEC sp_executesql @TempSql, N'@CardTitle NVARCHAR(128)', @CardTitle;
    
    -- Add TSQL.APP-specific parent relationships
    INSERT INTO #Nodes (TableName, TableColor, Category, ToolTip)
    SELECT DISTINCT 
        ParentCard,
        'lightblue',
        'Parent',
        'Parent Card: ' + ParentCard + ' (App relationship via: ' + RefColumn + ')'
    FROM #CardChildren
    WHERE ChildCard = @CardTitle
    AND ParentCard <> @CardTitle
    AND NOT EXISTS (SELECT 1 FROM #Nodes WHERE TableName = ParentCard);
    
    -- Add links for TSQL.APP-specific parent relationships
    INSERT INTO #Links (FromTable, ToTable)
    SELECT DISTINCT 
        ChildCard,
        ParentCard
    FROM #CardChildren
    WHERE ChildCard = @CardTitle
    AND ParentCard <> @CardTitle;
    
    -- Add TSQL.APP-specific child relationships
    INSERT INTO #Nodes (TableName, TableColor, Category, ToolTip)
    SELECT DISTINCT 
        ChildCard,
        'lightgreen',
        'Child',
        'Child Card: ' + ChildCard + ' (App relationship via: ' + RefColumn + ')'
    FROM #CardChildren
    WHERE ParentCard = @CardTitle
    AND ChildCard <> @CardTitle
    AND NOT EXISTS (SELECT 1 FROM #Nodes WHERE TableName = ChildCard);
    
    -- Add links for TSQL.APP-specific child relationships
    INSERT INTO #Links (FromTable, ToTable)
    SELECT DISTINCT 
        ChildCard,
        ParentCard
    FROM #CardChildren
    WHERE ParentCard = @CardTitle
    AND ChildCard <> @CardTitle;
    
    -- Convert to JSON format for the diagram
    SET @NodesJson = (
        SELECT 
            TableName AS [key],
            TableColor AS [color],
            Category AS [category],
            ToolTip AS [tooltip]
        FROM #Nodes
        FOR JSON PATH
    );
    
    SET @LinksJson = (
        SELECT 
            FromTable AS [from],
            ToTable AS [to]
        FROM #Links
        FOR JSON PATH
    );
    
    -- Combine into final JSON
    SET @FinalJson = N'{';
    SET @FinalJson = @FinalJson + N'"nodes": ' + ISNULL(@NodesJson, N'[]') + N',';
    SET @FinalJson = @FinalJson + N'"links": ' + ISNULL(@LinksJson, N'[]');
    SET @FinalJson = @FinalJson + N'}';
    
    -- Display the title
    SET @text = @Title;
    EXEC sp_api_modal_text @text=@text, @class=N'h3';
    
    -- Add legend for node colors
    SET @text = N'<div style="margin: 10px 0; font-size: 14px;"><span style="background-color: lightcoral; padding: 2px 8px; margin-right: 10px; border: 1px solid #666;">Current Table</span><span style="background-color: lightblue; padding: 2px 8px; margin-right: 10px; border: 1px solid #666;">Parent Tables</span><span style="background-color: lightgreen; padding: 2px 8px; margin-right: 10px; border: 1px solid #666;">Child Tables</span></div>';
    EXEC sp_api_modal_html @name=N'legend', @html=@text, @iframe=0;
    
    -- Show ERD in iframe
    SET @html = N'
    <!DOCTYPE html>
    <html lang="en">
    <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>ERD - ' + @CardTitle + N'</title>
        <script src="https://cdnjs.cloudflare.com/ajax/libs/gojs/2.3.11/go.js"></script>
        <style>
            html, body { height: 100%; margin: 0; }
            #diagramDiv { width: 100%; height: 100%; background-color: #f8f9fa; }
        </style>
    </head>
    <body>
        <div id="diagramDiv"></div>
        <script>
            // GoJS initialization
            var $ = go.GraphObject.make;
            var myDiagram = $(go.Diagram, "diagramDiv", {
                "undoManager.isEnabled": true,
                layout: $(go.ForceDirectedLayout, { 
                    defaultSpringLength: 100,
                    defaultElectricalCharge: 100
                })
            });

            // Define tooltips
            var tooltipTemplate = $(go.Adornment, "Auto",
                $(go.Shape, { fill: "#FFFFCC" }),
                $(go.TextBlock, { margin: 4 },
                    new go.Binding("text", "", function(data) { return data.tooltip; }))
            );

            // Node template
            myDiagram.nodeTemplate = $(go.Node, "Auto",
                { 
                    locationSpot: go.Spot.Center,
                    toolTip: tooltipTemplate
                },
                new go.Binding("location", "loc"),
                $(go.Shape, "Rectangle", { 
                    fill: "lightblue", 
                    strokeWidth: 2,
                    width: 160,
                    height: 40
                },
                new go.Binding("fill", "color")),
                $(go.TextBlock, { 
                    margin: 10, 
                    font: "bold 14px sans-serif",
                    stroke: "black",
                    width: 140,
                    textAlign: "center",
                    wrap: go.TextBlock.WrapFit
                },
                new go.Binding("text", "key"))
            );

            // Link template
            myDiagram.linkTemplate = $(go.Link,
                { routing: go.Link.AvoidsNodes, corner: 5 },
                $(go.Shape, { strokeWidth: 1.5 }),
                $(go.Shape, { toArrow: "Standard", stroke: null, fill: "black" })
            );

            // Load the data
            var erdData = ' + @FinalJson + ';
            
            // Position the current table in the center
            if (erdData.nodes && erdData.nodes.length > 0) {
                for (var i = 0; i < erdData.nodes.length; i++) {
                    if (erdData.nodes[i].category === "Current") {
                        erdData.nodes[i].loc = new go.Point(0, 0);
                        break;
                    }
                }
            }
            
            // Set the model
            myDiagram.model = new go.GraphLinksModel(erdData.nodes, erdData.links);
        </script>
    </body>
    </html>';
    
    -- Display the ERD using HTML iframe
    EXEC sp_api_modal_html 
        @name=N'ERD', 
        @html=@html, 
        @iframe=1, 
        @height=N'768px'; --'600px';
    
    -- Generate automatic markdown description
    -- Count tables by category
    DECLARE @ParentCount INT = (SELECT COUNT(*) FROM #Nodes WHERE Category = 'Parent');
    DECLARE @ChildCount INT = (SELECT COUNT(*) FROM #Nodes WHERE Category = 'Child');
    DECLARE @RelationshipCount INT = (SELECT COUNT(*) FROM #Links);
    DECLARE @ParentTables NVARCHAR(MAX) = '';
    DECLARE @ChildTables NVARCHAR(MAX) = '';
    
    -- Get list of parent tables
    SELECT @ParentTables = @ParentTables + '- ' + TableName + CHAR(13) + CHAR(10)
    FROM #Nodes 
    WHERE Category = 'Parent'
    ORDER BY TableName;
    
    -- Get list of child tables
    SELECT @ChildTables = @ChildTables + '- ' + TableName + CHAR(13) + CHAR(10)
    FROM #Nodes 
    WHERE Category = 'Child'
    ORDER BY TableName;
    
    -- Build the markdown description
    SET @description = N'## Database Relationship Diagram for ' + @CurrentTable + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10);
    SET @description = @description + N'This diagram shows the database relationships for the **' + @CurrentTable + '** table.' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10);
    SET @description = @description + N'### Overview' + CHAR(13) + CHAR(10);
    SET @description = @description + N'- **Central table** (shown in red): ' + @CurrentTable + CHAR(13) + CHAR(10);
    
    IF @ParentCount > 0
        SET @description = @description + N'- **Parent tables** (shown in blue): ' + CAST(@ParentCount AS NVARCHAR) + ' tables that ' + @CurrentTable + ' references' + CHAR(13) + CHAR(10);
    ELSE
        SET @description = @description + N'- **Parent tables**: None found' + CHAR(13) + CHAR(10);
    
    IF @ChildCount > 0
        SET @description = @description + N'- **Child tables** (shown in green): ' + CAST(@ChildCount AS NVARCHAR) + ' tables that reference ' + @CurrentTable + CHAR(13) + CHAR(10);
    ELSE
        SET @description = @description + N'- **Child tables**: None found' + CHAR(13) + CHAR(10);
    
    SET @description = @description + N'- **Total relationships**: ' + CAST(@RelationshipCount AS NVARCHAR) + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10);
    
    IF @ParentCount > 0
    BEGIN
        SET @description = @description + N'### Parent Tables' + CHAR(13) + CHAR(10);
        SET @description = @description + N'The following tables are referenced by ' + @CurrentTable + ':' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10);
        SET @description = @description + @ParentTables + CHAR(13) + CHAR(10);
    END
    
    IF @ChildCount > 0
    BEGIN
        SET @description = @description + N'### Child Tables' + CHAR(13) + CHAR(10);
        SET @description = @description + N'The following tables reference ' + @CurrentTable + ':' + CHAR(13) + CHAR(10) + CHAR(13) + CHAR(10);
        SET @description = @description + @ChildTables + CHAR(13) + CHAR(10);
    END
    
    SET @description = @description + N'### How to Read This Diagram' + CHAR(13) + CHAR(10);
    SET @description = @description + N'- **Arrows point from** the table that contains the foreign key **to** the referenced table' + CHAR(13) + CHAR(10);
    SET @description = @description + N'- **Hover over a table** to see additional information' + CHAR(13) + CHAR(10);
    SET @description = @description + N'- **Tables are color-coded**: red (current table), blue (parent tables), green (child tables)' + CHAR(13) + CHAR(10);
    
    -- Display the markdown description
    EXEC sp_api_modal_text @text=@description, @markdown=1;
    
    -- Add a close button
    SET @ButtonValue = N'Close';
    EXEC sp_api_modal_button 
        @name=N'@ButtonValue', 
        @value=@ButtonValue, 
        @valueout=@ButtonValueOut OUT,
        @class=N'btn-secondary';
    
    -- Handle button click
    IF @ButtonValueOut IS NOT NULL
    BEGIN
        SET @Message = N'ERD closed';
        EXEC sp_api_toast @text=@Message, @class=N'btn-info';
        EXEC sp_api_modal_clear;
        RETURN;
    END
    

