-- Step 1: Declare necessary variables
DECLARE @CorrectEmoji NVARCHAR(10);
DECLARE @CorrectWord NVARCHAR(50);  -- The word in the target language
DECLARE @UserChoice NVARCHAR(50);
DECLARE @Result NVARCHAR(MAX);
DECLARE @Quit NVARCHAR(MAX);
DECLARE @Score INT;
DECLARE @Round INT;
DECLARE @GameState NVARCHAR(MAX);
DECLARE @TimeLeft INT = 10;
DECLARE @Language NVARCHAR(50) = 'Spanish'; -- You can change this to another language
DECLARE @Hint NVARCHAR(50) = NULL; -- Optional hint

-- Step 2: Synchronize values with the client
EXEC sp_api_modal_get_value @name='@GameState', @value=@GameState OUT;

-- Step 3: Parse game state if it exists
IF @GameState IS NOT NULL
BEGIN
    SET @Score = JSON_VALUE(@GameState, '$.Score');
    SET @Round = JSON_VALUE(@GameState, '$.Round');
    SET @CorrectEmoji = JSON_VALUE(@GameState, '$.CorrectEmoji');
    SET @CorrectWord = JSON_VALUE(@GameState, '$.CorrectWord');
    SET @TimeLeft = JSON_VALUE(@GameState, '$.TimeLeft');
END
ELSE
BEGIN
    SET @Score = 0;
    SET @Round = 1;
    SET @CorrectEmoji = NULL;
    SET @TimeLeft = 10;
END

-- Step 4: Process user's choice and select a new emoji if needed
EXEC sp_api_modal_get_value @name='@UserChoice', @value=@UserChoice OUT;
IF @UserChoice IS NOT NULL
BEGIN
    DECLARE @ResultText NVARCHAR(MAX);
    IF @UserChoice = @CorrectWord
    BEGIN
        SET @ResultText = N'🎉 Correct! You earned a point!';
        SET @Score = @Score + 1;
        EXEC sp_api_modal_beep @frequency=1000, @duration=500;
    END
    ELSE
    BEGIN
        SET @ResultText = N'😢 Sorry, the correct word was: ' + @CorrectWord;
        EXEC sp_api_modal_beep @frequency=200, @duration=500;
    END
    SET @Result = @ResultText;
    SET @Round = @Round + 1;
    SET @CorrectEmoji = NULL;
    SET @TimeLeft = 10;
END

-- Step 5: Timer logic
IF @TimeLeft > 0
BEGIN
    SET @TimeLeft = @TimeLeft - 1;
    EXEC sp_api_modal_beep @frequency=500, @duration=200;
END
ELSE IF @UserChoice IS NULL
BEGIN
    SET @Result = N'⏰ Time''s up! The correct word was: ' + @CorrectWord;
    EXEC sp_api_modal_beep @frequency=200, @duration=500;
    SET @Round = @Round + 1;
    SET @CorrectEmoji = NULL;
    SET @TimeLeft = 10;
END

-- Step 6: Select new emoji and word in the target language
IF @CorrectEmoji IS NULL
BEGIN
    DECLARE @EmojiWordList TABLE (Emoji NVARCHAR(10), Word NVARCHAR(50));
    
    -- Populate with words in the target language (e.g., Spanish)
    INSERT INTO @EmojiWordList VALUES
        (N'😀', N'Cara Feliz'), -- Happy Face
        (N'🐱', N'Gato'),       -- Cat
        (N'🌳', N'Árbol'),      -- Tree
        (N'🍕', N'Pizza'),      -- Pizza
        (N'🚗', N'Coche');      -- Car

    IF @Round > 5
        INSERT INTO @EmojiWordList VALUES (N'⚽', N'Fútbol'), (N'🎸', N'Guitarra');
    
    IF @Round > 10
        INSERT INTO @EmojiWordList VALUES (N'🚀', N'Cohete'), (N'🌈', N'Arcoíris');
    
    -- Randomly select a new emoji and word
    SELECT TOP 1 
        @CorrectEmoji = Emoji,
        @CorrectWord = Word
    FROM @EmojiWordList
    ORDER BY NEWID();
    
    -- Optional hint (e.g., the word in English)
    IF @Language = 'Spanish'
    BEGIN
        SET @Hint = CASE @CorrectWord
            WHEN 'Cara Feliz' THEN N'Happy Face'
            WHEN 'Gato' THEN N'Cat'
            WHEN 'Árbol' THEN N'Tree'
            WHEN 'Pizza' THEN N'Pizza'
            WHEN 'Coche' THEN N'Car'
            WHEN 'Fútbol' THEN N'Soccer'
            WHEN 'Guitarra' THEN N'Guitar'
            WHEN 'Cohete' THEN N'Rocket'
            WHEN 'Arcoíris' THEN N'Rainbow'
        END;
    END
END

-- Step 7: Prepare display texts
DECLARE @GameTitle NVARCHAR(MAX) = CONCAT(N'🎮 Learn ', @Language, N' Words 🎮');
DECLARE @ScoreRoundText NVARCHAR(MAX) = CONCAT(N'Score: ', CAST(@Score AS NVARCHAR), N' | Round: ', CAST(@Round AS NVARCHAR));
DECLARE @TimerText NVARCHAR(MAX) = CONCAT(N'Time left: ', CAST(@TimeLeft AS NVARCHAR), N' seconds');
