DECLARE @HintText NVARCHAR(MAX) = CONCAT(N'Hint: ', @Hint); -- Display the hint

-- Step 8: Display game information
EXEC sp_api_modal_text @text=@GameTitle, @class='h2';
EXEC sp_api_modal_text @text=@ScoreRoundText, @class='h4';
EXEC sp_api_modal_text @text=@TimerText, @class='h5';
EXEC sp_api_modal_text @text=@QuestionText, @class='h3';
EXEC sp_api_modal_text @text=@HintText, @class='h6'; -- Display hint for learning assistance

-- Step 9: Display result if there is one
IF @Result IS NOT NULL
BEGIN
    EXEC sp_api_modal_text @text=@Result, @class='h3';
    SET @Result = NULL;
END

-- Step 10: Create buttons for each description option using sp_api_modal_choose
DECLARE @ChoiceList NVARCHAR(MAX) = N'Cara Feliz,Gato,Árbol,Pizza,Coche';
IF @Round > 5
    SET @ChoiceList = @ChoiceList + N',Fútbol,Guitarra';
IF @Round > 10
    SET @ChoiceList = @ChoiceList + N',Cohete,Arcoíris';

EXEC sp_api_modal_choose @name='@UserChoice', @list=@ChoiceList, @value=@UserChoice OUT;--, @class='btn-group';

-- Step 11: Add a button to quit the game
DECLARE @QuitButtonText NVARCHAR(20) = N'End Game 🏁';
DECLARE @QuitButtonClass NVARCHAR(20) = 'btn-danger';
EXEC sp_api_modal_button @name='quit', @value=@QuitButtonText, @valueout=@Quit OUT, @class=@QuitButtonClass;

-- Step 12: Handle game end
IF @Quit IS NOT NULL
BEGIN
    DECLARE @FinalScoreText NVARCHAR(MAX) = CONCAT(N'Thanks for playing! Your final score: ', CAST(@Score AS NVARCHAR));
    EXEC sp_api_modal_text @text=@FinalScoreText, @class='h2';
    EXEC sp_api_modal_clear;
    RETURN;
END

-- Step 13: Prepare and save game state for the next roundtrip
SET @GameState = (SELECT @Score AS Score, @Round AS Round, @CorrectEmoji AS CorrectEmoji, @CorrectWord AS CorrectWord, @TimeLeft AS TimeLeft FOR JSON PATH, WITHOUT_ARRAY_WRAPPER);
EXEC sp_api_modal_value @name='@GameState', @value=@GameState;

