        RETURN;
    END
    
    -- Next button clicked - transition to Step 2
    IF @NextButton IS NOT NULL
    BEGIN
        -- Get JSON for state persistence
        SELECT @ProductsJson = (SELECT * FROM #Products FOR JSON PATH);
        
        -- Prepare state for next step
        SET @JsonState = (
            SELECT 
                [@Step]=2, 
                [@ProductsJson]=@ProductsJson,
                [@MaxProductID]=@MaxProductID  -- Persist @MaxProductID in state
            FOR JSON PATH, WITHOUT_ARRAY_WRAPPER
        );
        
        -- Move to next step
        EXEC sp_api_modal_restart @values = @JsonState;
        RETURN;
    END
END

-- STEP 2: Inventory management - delete products
IF @Step = 2
BEGIN
    -- Create inventory table with calculated fields
    CREATE TABLE #Inventory (
        ProductID INT,
        ProductName NVARCHAR(100),
        Price DECIMAL(10,2),
        Quantity INT,
        Value DECIMAL(10,2),
        Status NVARCHAR(20)
    );
    
    -- Populate the inventory table from JSON
    IF @ProductsJson IS NOT NULL
    BEGIN
        SET @text = N'INSERT INTO #Inventory
        SELECT 
            p.ProductID,
            p.ProductName,
            p.Price,
            p.Quantity,
            p.Price * p.Quantity AS Value,
            CASE 
                WHEN p.Quantity = 0 THEN N''Out of Stock''
                WHEN p.Quantity < 5 THEN N''Low Stock''
                WHEN p.Quantity >= 20 THEN N''Well Stocked''
                ELSE N''In Stock''
            END AS Status
        FROM OPENJSON(''' + @ProductsJson + ''')
        WITH (
            ProductID INT ''$.ProductID'',
            ProductName NVARCHAR(100) ''$.ProductName'',
            Price DECIMAL(10,2) ''$.Price'',
            Quantity INT ''$.Quantity''
        ) p';
        
        EXEC sp_api_exec @sql = @text;
    END
    
    -- Display Step 2 interface
    SET @StepTitle = N'Step 2: Manage Inventory';
    SET @DisplayText = N'Remove products from inventory';
    
    EXEC sp_api_modal_text @text = @StepTitle, @class = N'h3';
    EXEC sp_api_modal_text @text = @DisplayText, @class = N'text-muted';
    
    -- Show current inventory
    EXEC sp_api_modal_text @text = N'Current Inventory:', @class = N'mt-3 mb-2 font-weight-bold';
    EXEC sp_api_modal_table @name = N'@InventoryTable', @tmptable = N'#Inventory', @orderby = N'ORDER BY ProductID', @print = 1;
    
    -- Debug mode toggle with direct post to update immediately
    EXEC sp_api_modal_switch 
        @name = N'@DebugMode', 
        @value = @DebugMode OUT, 
        @label = N'Debug Mode', 
        @inline = 1,
        @direct_post = 1;  -- This causes immediate post when toggled
        
    -- Display debug information if enabled
    IF @DebugMode = 1
    BEGIN
        EXEC sp_api_modal_show_json_values
    END
    
    -- Remove product form
    EXEC sp_api_modal_text @text = N'Remove Product:', @class = N'mt-4 mb-2 font-weight-bold';
    EXEC sp_api_modal_input @name = N'@DeleteProductID', @value = @DeleteProductIDText OUT, 
                           @placeholder = N'Enter Product ID to delete', @type = N'number';
    
    -- Buttons
    EXEC sp_api_modal_button @name = N'@DeleteButton', @value = N'Delete Product', 
                            @valueout = @DeleteButton OUT, @class = N'btn-danger', @inline = 1;
    EXEC sp_api_modal_button @name = N'@BackButton', @value = N'← Back', 
                            @valueout = @BackButton OUT, @class = N'btn-secondary ml-2', @inline = 1;
    
    -- Delete Product button clicked
    IF @DeleteButton IS NOT NULL
    BEGIN
        -- Validate input
        IF @DeleteProductID IS NULL
        BEGIN
            SET @Message = N'Please enter a valid Product ID';
            EXEC sp_api_toast @text = @Message, @class = N'btn-warning';
            EXEC sp_api_modal_value @name = N'@DeleteButton', @value = NULL;
            RETURN;
        END
        
        -- Check if product exists using temporary table approach
        DECLARE @ProductExistsTable TABLE (ProductExists BIT);
        
        SET @text = N'
        SELECT CASE
            WHEN EXISTS (SELECT 1 FROM #Inventory WHERE ProductID = ' + CAST(@DeleteProductID AS NVARCHAR(10)) + ')
            THEN 1
            ELSE 0
        END AS ProductExists';
        
        INSERT INTO @ProductExistsTable
        EXEC sp_api_exec @sql = @text;
        
        -- Get the existence result
        SELECT @ProductExists = ProductExists FROM @ProductExistsTable;
        
        -- Get product name using a table variable
        DECLARE @ProductNameTable TABLE (ProductName NVARCHAR(100));
        
        SET @text = N'
        SELECT ProductName
        FROM #Inventory 
        WHERE ProductID = ' + CAST(@DeleteProductID AS NVARCHAR(10));
        
        INSERT INTO @ProductNameTable
        EXEC sp_api_exec @sql = @text;
        
        -- Get the product name
        SELECT @DeletedProductName = ProductName FROM @ProductNameTable;
        
        IF @ProductExists = 0
        BEGIN
            SET @Message = N'Product ID not found';
            EXEC sp_api_toast @text = @Message, @class = N'btn-warning';
            RETURN;
        END
        
        -- Create temporary table for products without the deleted one
        CREATE TABLE #UpdatedProducts (
            ProductID INT,
            ProductName NVARCHAR(100),
            Price DECIMAL(10,2),
            Quantity INT
        );
        
        SET @text = N'
        INSERT INTO #UpdatedProducts
        SELECT ProductID, ProductName, Price, Quantity
        FROM #Inventory
        WHERE ProductID != ' + CAST(@DeleteProductID AS NVARCHAR(10));
        
        EXEC sp_api_exec @sql = @text;
        
        -- Get JSON for state persistence
        SELECT @ProductsJson = (SELECT * FROM #UpdatedProducts FOR JSON PATH);
        
        -- If all products were deleted, set empty array
        IF @ProductsJson IS NULL
            SET @ProductsJson = N'[]';
        
        -- Reset input field and prepare state
        SET @JsonState = (
            SELECT 
                [@Step]=2, 
                [@ProductsJson]=@ProductsJson,
                [@MaxProductID]=@MaxProductID,  -- Persist @MaxProductID in state
                [@DeleteProductID]=NULL
            FOR JSON PATH, WITHOUT_ARRAY_WRAPPER
        );
        
        -- Show success message
        SET @Message = N'Product "' + ISNULL(@DeletedProductName, N'Unknown') + N'" deleted successfully!';
        EXEC sp_api_toast @text = @Message, @class = N'btn-success';
        
        -- Restart with updated state
        EXEC sp_api_modal_restart @values = @JsonState;
        RETURN;
    END
    
    -- Back button clicked - return to Step 1
    IF @BackButton IS NOT NULL
    BEGIN
        -- Prepare state for previous step
        SET @JsonState = (
            SELECT 
                [@Step]=1, 
                [@ProductsJson]=@ProductsJson,
                [@MaxProductID]=@MaxProductID  -- Persist @MaxProductID in state
            FOR JSON PATH, WITHOUT_ARRAY_WRAPPER
        );
        
        -- Move back to previous step
        EXEC sp_api_modal_restart @values = @JsonState;
        RETURN;
    END
END

